const INITIAL_EXPENSES = localStorage.getItem('myExpenses') ? JSON.parse(localStorage.getItem('myExpenses')) : [];

function App() {
  console.log(' >>>', INITIAL_EXPENSES);
  const [expenses, setExpenses] = useState(INITIAL_EXPENSES);

  const addNewExpenseHandler = (enteredData) => {
    setExpenses((prevState) => [enteredData, ...prevState]);
    localStorage.setItem('myExpenses', JSON.stringify([enteredData, ...expenses]));
  }

  return (
    <div>
      <NewExpense onAddNewExpense={addNewExpenseHandler} />
      <Expenses items={expenses} />
    </div>
  );
}

export default App;
