import './ExpensesList.css';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import { useState } from 'react';

export default function ExpensesList({items}) {
  const [dateFiltered, setDateFiltered] = useState(2020);
  const filterHandler = (date) => {
    setDateFiltered(date);

    const filter = items.filter((item) => item.date.getFullYear() === dateFiltered);
    console.log(' >>>', date);
    console.log('items.filter((item)>>>', filter);
  }

  return (
    <div>
      <Card className='expenses'>
        <ExpenseFilter defaultDate={dateFiltered} onChangeDate={filterHandler} />

        {items.filter((item) => item.date.getFullYear() === dateFiltered).map((item) => (
          <ExpenseItem 
            key={item.id}
            date={item.date} 
            title={item.title} 
            amount={item.amount} 
          />
        ))}
      </Card>
    </div>
  )
}
